  // ----------------------------------------------------------------------------
  // open menu
  // ----------------------------------------------------------------------------

function openMenu() {
  document.querySelector("#toggle-menu-mobile").classList.contains("d-sm-none")
    ? document
        .querySelector("#toggle-menu-mobile")
        .classList.remove("d-sm-none")
    : document
        .querySelector("#toggle-menu-mobile")
        .classList.remove("d-sm-none");
}

  // ----------------------------------------------------------------------------
  //  menu 
  // ----------------------------------------------------------------------------


function closeMenu() {
  document.querySelector("#toggle-menu-mobile").classList.contains("d-sm-none"),
    document.querySelector("#toggle-menu-mobile").classList.add("d-sm-none");
}

