
var data = {};
function selectInfo() {
  return (
    (data.Motif = document.querySelector("#Motif")?.value),
    (data.Civilite = document.querySelector("#Civilite")?.value),
    (data.Nom = document.querySelector("#Nom")?.value),
    (data.Prenom = document.querySelector("#Prenom")?.value),
    (data.Email = document.querySelector("#Email")?.value),
    (data.Mobile = document.querySelector("#Mobile")?.value),
    data.Civilite
      ? data.Motif
        ? data.Mobile
          ? data.Nom
            ? data.Prenom
              ? data.Email
                ? (document
                    .querySelector("#loading-indicator")
                    .classList.remove("d-none"),
                  console.log(data),
                  void axios
                    .post("/sendmail", { ...data })
                    .then(function (e) {
                      console.log(e.data);
                      const o = e.data;
                      document
                        .querySelector("#loading-indicator")
                        .classList.add("d-none"),
                        "success" == o.code
                          ? Swal.fire(
                              "JVGO Confirmation!",
                              "Notre équipe est là pour vous conseiller. Vous recevrez une réponse en moins de 24 heures.",
                              "success"
                            )
                          : ((document.querySelector(
                              "#error-display"
                            ).innerText = o.message),
                            document
                              .querySelector("#error-display")
                              .scrollIntoView({
                                behavior: "smooth",
                                block: "nearest",
                              }),
                            setTimeout(() => {
                              document
                                .querySelector("#error-display")
                                .classList.add("d-none");
                            }, 8e3));
                    })
                    .catch(function (e) {
                      console.log(e),
                        document
                          .querySelector("#loading-indicator")
                          .classList.add("d-none");
                    }))
                : (document
                    .querySelector("#error-display")
                    .classList.remove("d-none"),
                  (document.querySelector("#error-display").innerText =
                    "Veuillez renseigner le champ Email"),
                  document
                    .querySelector("#error-display")
                    .scrollIntoView({ behavior: "smooth", block: "nearest" }),
                  void setTimeout(() => {
                    document
                      .querySelector("#error-display")
                      .classList.add("d-none");
                  }, 8e3))
              : (document
                  .querySelector("#error-display")
                  .classList.remove("d-none"),
                (document.querySelector("#error-display").innerText =
                  "Veuillez renseigner le champ Prenom"),
                document
                  .querySelector("#error-display")
                  .scrollIntoView({ behavior: "smooth", block: "nearest" }),
                void setTimeout(() => {
                  document
                    .querySelector("#error-display")
                    .classList.add("d-none");
                }, 8e3))
            : (document
                .querySelector("#error-display")
                .classList.remove("d-none"),
              (document.querySelector("#error-display").innerText =
                "Veuillez renseigner le champ Nom"),
              document
                .querySelector("#error-display")
                .scrollIntoView({ behavior: "smooth", block: "nearest" }),
              void setTimeout(() => {
                document
                  .querySelector("#error-display")
                  .classList.add("d-none");
              }, 8e3))
          : (document
              .querySelector("#error-display")
              .classList.remove("d-none"),
            (document.querySelector("#error-display").innerText =
              "Veuillez renseigner le champ Mobile"),
            document
              .querySelector("#error-display")
              .scrollIntoView({ behavior: "smooth", block: "nearest" }),
            void setTimeout(() => {
              document.querySelector("#error-display").classList.add("d-none");
            }, 8e3))
        : (document.querySelector("#error-display").classList.remove("d-none"),
          (document.querySelector("#error-display").innerText =
            "Veuillez renseigner le champ Motif"),
          document
            .querySelector("#error-display")
            .scrollIntoView({ behavior: "smooth", block: "nearest" }),
          void setTimeout(() => {
            document.querySelector("#error-display").classList.add("d-none");
          }, 8e3))
      : (document.querySelector("#error-display").classList.remove("d-none"),
        (document.querySelector("#error-display").innerText =
          "Veuillez selectionner la civilité"),
        document
          .querySelector("#error-display")
          .scrollIntoView({ behavior: "smooth", block: "nearest" }),
        void setTimeout(() => {
          document.querySelector("#error-display").classList.add("d-none");
        }, 8e3))
  );
}
